import axios from "axios";
import {appConstants} from "../constants/constant";

const API = 'http://localhost:8089';

export const Maintenance = (eu, success, failure) => {
    const MaintenancePromise = axios.post(`${API}/maintenances`, eu)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });

    return {
        type: appConstants,
        payload: MaintenancePromise
    };
};



export const getMaintenance = () => {
    const getMaintenancePromise = axios.get(`${API}/maintenances/notFinished`, {withCredentials: true});


    return {
        type: appConstants.GET_EMAIL_US,
        payload: getMaintenancePromise
    };
};
