import {appConstants} from "../constants/constant";
import axios from 'axios';

export const checkLogin = () => {
    const checkLoginPromise = fetch('http://localhost:8089/checklogin', {credentials: 'include'})
        .then(res => res.json())
        .catch();
    return {
        type: appConstants.CHECK_LOGIN,
        payload: checkLoginPromise
    };
};


export const login = (user, success, failure) => {
    // we can use a library called qs   qs.stringify(user) to get a urlencoded form data
    const userFormData = new FormData();
    userFormData.append('username', user.username);
    userFormData.append('password', user.password);
    // ES6 fetch API, async, await
    const loginPromise = fetch(
        'http://localhost:8089/login',
        {
            method: 'POST',
            body: userFormData,
            credentials: 'include'
        }
        )
            .then(res => res.json())
            .then(res => {
                console.log(res);
                res.success ?
                    // check success is a function
                    typeof success === 'function' && success() :
                    typeof failure === 'function' && failure();
                return res;
            })
            .catch(err => {
                typeof failure === 'function' && failure(err);
                return err;
            })
    ;
    return {
        type: appConstants.LOGIN,
        payload: loginPromise
    };
};



const API = 'http://localhost:8089';

export const register = (user, success, failure) => {
    const registerPromise = axios.post(`${API}/users`, user)
        .then(res => {
            // TODO: check success and failure and make sure it's a function (tech debt)
            // res.data.success ? success() : failure();
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
                return res;
        })
        .catch(err => {
                typeof failure === 'function' && failure(err);
                return err;
        });

    return {
        type: appConstants.REGISTER,
        payload: registerPromise
    };
};


export const tourReserve = (tr, success, failure) => {
    const trPromise = axios.post(`${API}/tourReservations`, tr)
        .then(res => {
            // TODO: check success and failure and make sure it's a function (tech debt)
            // res.data.success ? success() : failure();
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });

    return {
        type: appConstants.REGISTER,
        payload: trPromise
    };
};



