import axios from "axios";
import {appConstants} from "../constants/constant";


const API = 'http://localhost:8089';
export const issueBill = (ib, success, failure) => {
    const issueBillPromise = axios.put(`${API}/paymentHistories`, ib)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });


    return {
        type: appConstants.ISSUE_BILL,
        payload: issueBillPromise
    };
};


export const issueMonthly = (user_id, success, failure) => {
    const deActivatePromise = axios.put(`${API}/users/deActivate/${user_id}`)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });


    return {
        type: appConstants.DE_ACTIVATE,
        payload: deActivatePromise
    };
};
