import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import SendIcon from '@material-ui/icons/Send';
import DraftsIcon from '@material-ui/icons/Drafts';
import Maintenance from "./Maintenance";
import ApplicationBasicInfo from "./application/ApplicationBasicInfo";
import ApplicationRoommates from "./application/ApplicationRoomates";
import Transaction from "./Transaction";
import {loadStripe} from "@stripe/stripe-js";

import {
    CardElement,
    Elements,
    useStripe,
    useElements,
} from '@stripe/react-stripe-js';
import ApplicationUnitInfo from "./application/ApplicationUnitInfo";



const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    // necessary for content to be below app bar
    toolbar: theme.mixins.toolbar,
    content: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.default,
        padding: theme.spacing(3),
    },
}));



const UserPage = () => {
    const classes = useStyles();
    const [component, setComponent] = useState('user');


    const stripePromise = loadStripe('pk_test_6pRNASCoBOKtIshFeQd4XMUh');

    return (
        <div className={classes.root}>
                <div className={classes.toolbar} />
                <List>
                    <ListItem button onClick={() => setComponent('application')}>
                        <ListItemIcon>
                            <SendIcon />
                        </ListItemIcon>
                        <ListItemText primary="Application" />
                    </ListItem>
                    <ListItem button onClick={() => setComponent('maintenance')}>
                        <ListItemIcon>
                            <DraftsIcon />
                        </ListItemIcon>
                        <ListItemText primary="Maintenance request" />
                    </ListItem>
                    <ListItem button onClick={() => setComponent('transactions')}>
                        <ListItemIcon>
                            <InboxIcon />
                        </ListItemIcon>
                        <ListItemText primary="Transactions" />
                    </ListItem>
                </List>
            <main className={classes.content}>
                <div className={classes.toolbar} />
                {
                    component === 'user' ?
                        <div>
                            <ApplicationBasicInfo />
                            <ApplicationUnitInfo />
                            <ApplicationRoommates />
                        </div>
                        :
                        component === 'maintenance' ?
                            <Maintenance />
                            :
                            <Elements stripe={stripePromise}>
                                <Transaction />
                            </Elements>
                }
            </main>
        </div>
    );
}

export default UserPage;



