import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import {useDispatch, useSelector} from "react-redux";
import {getPayments} from "../actions/userPayments.action";
import Button from "@material-ui/core/Button/Button";
import {getPendingPayments} from "../actions/userPendingPayments";
import {appConstants} from "../constants/constant";


const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});


const Transaction = () => {
    const classes = useStyles();
    const dispatch = useDispatch();
l
    const transactionState = useSelector(appState => {
        return {
            user: appState.user,
            userPayments: appState.userPayments

        };
    });

    const handleSubmit = () => {
        dispatch(getPayments(transactionState.user.id));
    }




        return (
            <div>
            <div>
                <Button onClick = {handleSubmit}>Show my payment histories</Button>
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Amount $</TableCell>
                                <TableCell align="left">Memo</TableCell>
                                <TableCell align="left">Date</TableCell>
                                <TableCell align="left">Status</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {userPayments.map((row) => (
                                <TableRow key={row.id}>
                                    <TableCell component="th" scope="row">
                                        {row.amount}
                                    </TableCell>
                                    <TableCell align="left">{row.memo}</TableCell>
                                    <TableCell align="left">{row.date}</TableCell>
                                    <TableCell align="left">{row.status}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
            <div>
            </div>
            </div>
        );



}

export default Transaction;
