import React, {useState} from 'react';
import Button from "@material-ui/core/Button/Button";
import {useDispatch} from "react-redux";
import {issueBill} from "../actions/issurBill.action";

const AdminIssueBill = () => {

    const dispatch = useDispatch();

    const [ph, setPh] = useState({
        uid: '',
        amount: '',
        memo: '',
        date: new Date()
    });

    const [month, setMonth] = useState({
        amount: '',
        memo: '',
        type: '',
        date: new Date()
    });

    const f1 = (event) => {
        const newPh = {...ph};
        newPh.uid = event.target.value;
        setPh(newPh);
    };

    const f2 = (event) => {
        const newPh = {...ph};
        newPh.amount = event.target.value;
        setPh(newPh);
    };

    const f3 = (event) => {
        const newPh = {...ph};
        newPh.memo = event.target.value;
        setPh(newPh);
    };

    const f4 = (event) => {
        const newPh = {...ph};
        newPh.amount = event.target.value;
        setPh(newPh);
    };

    const f5 = (event) => {
        const newPh = {...ph};
        newPh.memo = event.target.value;
        setPh(newPh);
    };

    const f6 = (event) => {
        const newPh = {...ph};
        newPh.type = event.target.value;
        setPh(newPh);
    };



    const single = (event) => {
        event.preventDefault();
        dispatch(issueBill(ph));

    }


    return (
        <div>
            <h3>Issue bill to a specific user</h3>
            <label>User_id:</label><br/>
            <input type="number" onChange={f1}/><br/>
            <label>Amount:</label><br/>
            <input type="number" onChange={f2}/><br/>
            <label>Memo:</label><br/>
            <input type="text" onChange={f3}/><br/>
            <Button color = "primary" onClick = {single}>Issue</Button>

            <h3>Issue monthly payments to active users</h3>
            <label>Amount:</label><br/>
            <input type="number" onChange={f4}/><br/>
            <label>Memo:</label><br/>
            <input type="number" onChange={f5}/><br/>
            <select name="type" id="cars" onChange={f6}>
                <option value="1">Studio</option>
                <option value="2">2x2</option>
                <option value="3">3x3</option>
                <x4option value="4">4</x4option>
            </select>

        </div>
    );


}

export default AdminIssueBill;
