import {makeStyles} from "@material-ui/core";


const useStyles = makeStyles(() => ({
    frame:{
        width: "10",
        height: "512"
    }
}));


class Locations extends Component {
    constructor(props) {
        super(props);
        this.state = {
            keyword: ''
        };
        const classes = useStyles();
        console.log(11111);
    }


    // componentWillMount() {
