import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import {Component} from "react";
import React from "react";

class MapContainer extends Component {


    render() {
        return (


            // Important! Always set the container height explicitly
                <div className={'text-center'}>
                            <div style={{width: "10", height: "512"}}>
                                <Map
                                    style={{width: "95%", height: "515", display:'flex', flexFlow: 'row nowrap'}}
                                    google={this.props.google} zoom={16}
                                    initialCenter={{
                                        lat: 40.33, lng: -74.57
                                    }}
                                    >
                                    <Marker position={{ lat: 40.33, lng: -74.57}} />

                                    <InfoWindow onClose={this.onInfoWindowClose}>
                                        <div>
                                            <h1>{this.state.selectedPlace.name}</h1>
                                        </div>
                                    </InfoWindow>


                                </Map>


                            </div>
                </div>
        );
    }
}


export default GoogleApiWrapper({
    apiKey: ("AIzaSyCD_NjwMKIxeMBW5-KhgH2cntfYZdTc2A0")
})(MapContainer)
