import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import {Component} from "react";
import React from "react";

class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    createMarkers = (places) => {
        console.log('Create markers');
    }


    fetchPlaces(mapProps, map) {
        const {google} = mapProps;
        const service = new google.maps.places.PlacesService(map);

        let request = {
            location: {
                lat: 40.33, lng: -74.57
            },
            rankBy: google.maps.places.RankBy.DISTANCE,
            // keyword: 'sushi'

            keyword: 'hotel'
        };

        console.log(service);
        // service.nearbySearch(request, this.nearbyCallback);


        service.nearbySearch(request, function(results, status) {
            if (status === google.maps.places.PlacesServiceStatus.OK) {
                this.createMarkers(results);
            }
            else {
                console.log("doesn't work");
            }
        });






        console.log('fetchPlaces');
    }








    render() {
        return (


            // Important! Always set the container height explicitly
                <div className={'text-center'}>
                            <div style={{width: "10", height: "512"}}>
                                <Map
                                    style={{width: "95%", height: "515", display:'flex', flexFlow: 'row nowrap'}}
                                    onReady={this.fetchPlaces}
                                    google={this.props.google} zoom={16}
                                    initialCenter={{
                                        lat: 40.33, lng: -74.57
                                    }}
                                    >
                                    <Marker position={{ lat: 40.33, lng: -74.57}} />

                                    <Marker onClick={this.onMarkerClick}
                                            name={'Current location'} />

                                    <InfoWindow
                                        marker={this.state.activeMarker}
                                        visible={this.state.showingInfoWindow}>
                                        <div>
                                            <h1>{this.state.selectedPlace.name}</h1>
                                        </div>
                                    </InfoWindow>

                                </Map>


                            </div>
                </div>
        );
    }
}


export default GoogleApiWrapper({
    apiKey: ("AIzaSyCD_NjwMKIxeMBW5-KhgH2cntfYZdTc2A0")
})(MapContainer)
