import axios from "axios";
import {appConstants} from "../constants/constant";

const API = 'http://localhost:8089';

export const putUserInfo = (ui, success, failure) => {
    const putUserInfoPromise = axios.put(`${API}/user-details`, ui)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });

    return {
        type: appConstants.GET_USER_INFO,
        payload: putUserInfoPromise
    };
};


export const getUserInfos = () => {
    const getUserInfosPromise = axios.get(`${API}/user-details`);

    return {
        type: appConstants.GET_USER_INFOS,
        payload: getUserInfosPromise
    };
};


export const getUserInfo = (user_id) => {
    const getUserInfoPromise = axios.get(`${API}/user-details/${user_id}`, {withCredentials: true});

    return {
        type: appConstants.GET_USER_INFO,
        payload: getUserInfoPromise
    };
};
