import axios from "axios";
import {appConstants} from "../constants/constant";

const API = 'http://localhost:8089';

export const putUserInfo = (ui, user_id) => {
    const putUserInfoPromise = axios.put(`${API}/user-details/${user_id}`, ui);

    return {
        type: appConstants.PUT_USER_INFO,
        payload: putUserInfoPromise
    };
};



export const getUserInfo = (user_id) => {
    const getUserInfoPromise = axios.get(`${API}/user-details/${user_id}`, {withCredentials: true});

    return {
        type: appConstants.GET_USER_INFO,
        payload: getUserInfoPromise
    };
};
