import React, {useState} from "react";
import TextField from "@material-ui/core/TextField/TextField";
import InputAdornment from "@material-ui/core/InputAdornment/InputAdornment";
import UsernameIcon from "@material-ui/core/SvgIcon/SvgIcon";
import Fab from "@material-ui/core/Fab/Fab";
import PasswordIcon from "@material-ui/icons/VpnKey"
import {withStyles} from "@material-ui/core";
import {useDispatch, useSelector} from "react-redux";
import {appConstants} from "../constants/constant";
import {register} from "../actions/auth.action";
import './Login.scss';



const styles = {
    root: {
        marginTop: '25px',
        '& .MuiInput-underline:before': {
            borderBottomColor: 'white'
        },
        '& .MuiInput-underline:after': {
            borderBottomColor: 'white'
        },
        '& .MuiInput-underline:before:hover': {
            borderBottomColor: 'white'
        },
        '& .MuiInput-underline:hover:not(.MuiInput-disabled):before': {
            borderBottomColor: 'white'
        },
        '& .MuiFormLabel-root': {
            color: 'white'
        }
    },
    input: {
        color: 'white'
    },
    button: {
        '&.MuiButtonBase-root': {
            margin: '50px 0 25px'
        }
    }
};



const Register = (props) => {

    const {classes} = props;
    // // React hooks  React.useState()
    const [user, setUser] = useState({
        username: '',
        password: ''
    });
    const [open, setOpen] = useState(false);
    //
    const dispatch = useDispatch();
    // const loginState = useSelector(appState => {
    //     return {
    //         user: appState.user,
    //         msg: appState.user ?
    //             appConstants.LOGIN_SUCCESS_MESSAGE + user.username :
    //             appConstants.LOGIN_FAILURE_MESSAGE
    //     };
    // });
    //
    const handleFormControl = (event) => {
        const newUser = {...user};
        newUser[event.target.name] = event.target.value;
        setUser(newUser);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(register(
            user,
            () => {
                setOpen(true);
                setTimeout(() => {
                    props.history.goBack();
                }, 2000);
            },
            () => setOpen(true)
        ));
    };



    return (
        <div>
            <form className="login-form" onSubmit={handleSubmit}>
                <TextField
                    name="username"
                    // className={classes.root}
                    label="Username"
                    value={user.username}
                    onChange={handleFormControl}
                    InputProps={{
                        className: classes.input,
                        startAdornment: (
                            <InputAdornment position="start">
                                <UsernameIcon />
                            </InputAdornment>
                        ),
                    }}
                />

                <TextField
                    name="password"
                    // className={classes.root}
                    label="Password"
                    type="password"
                    value={user.password}
                    onChange={handleFormControl}
                    InputProps={{
                        className: classes.input,
                        startAdornment: (
                            <InputAdornment position="start">
                                <PasswordIcon />
                            </InputAdornment>
                        )
                    }}
                />

                {/*<TextField*/}
                    {/*name="confirmed-password"*/}
                    {/*// className={classes.root}*/}
                    {/*label="confirmedPassword"*/}
                    {/*type="password"*/}
                    {/*// value={user.password}*/}
                    {/*onChange={handleFormControl}*/}
                    {/*InputProps={{*/}
                        {/*className: classes.input,*/}
                        {/*startAdornment: (*/}
                            {/*<InputAdornment position="start">*/}
                                {/*<PasswordIcon />*/}
                            {/*</InputAdornment>*/}
                        {/*)*/}
                    {/*}}*/}
                {/*/>*/}

                <Fab
                    variant="extended"
                    size="medium"
                    color="secondary"
                    aria-label="Register"
                    className={classes.button}
                    type="submit"
                    // disabled={!user.username || !user.password}
                >
                    Register
                </Fab>
            </form>
        </div>

    );
}

export default withStyles(styles)(Register);
