import React, {Component} from "react";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
import * as Yup from 'yup';
import TextField from "@material-ui/core/TextField/TextField";
import Select from "@material-ui/core/Select/Select";



const EditProductSchema = Yup.object().shape({
    firstName: Yup.string()
        .required('First name is required'),
    lastName: Yup.string()
        .required('Last brand is required'),
    emailAddress: Yup.string()
        .required('Email address is required'),
    phone: Yup.number()
        .required('Phone number is required'),
});



class TourScheduler extends Component {
    state = {
        floorPlan : '',
        date: new Date()
    }

    setFloorPlan = event => this.setState({floorPlan : event.target.value});

    handleChange = date => this.setState({ date });

    // static renderField({field, form: {errors, touched}, ...props}) {
    //     // console.log(field);
    //     return (
    //         <TextField
    //             {...field}
    //             {...props}
    //             InputProps={{
    //                 startAdornment:
    //                     field.name === 'price' && <InputAdornment position="start">$</InputAdornment>
    //             }}
    //             error={errors[field.name] && touched[field.name]}
    //             helperText={touched[field.name] && errors[field.name]}
    //         />
    //     );
    // }


    render() {
        return (
            <div>
                <p>Is there a particular floor plan you would like to tour?</p>
                <select onChange={this.setFloorPlan}>
                    <option>No, Thank you</option>
                    <option>1x1(1 bed, 1 bath)</option>
                    <option>2x2(2 bed, 2 bath)</option>
                    <option>3x3(3 bed, 3 bath)</option>
                    <option>4x4(4 bed, 4 bath)</option>
                </select>
                <p>{this.state.floorPlan}</p>
                <DatePicker
                    selected={this.state.date}
                    onChange={this.handleChange}
                    minDate={new Date((new Date()).valueOf() + 1000*3600*24)}
                />
                <form>
                    <TextField  label="First Name"
                                variant="outlined"
                                error={this.state.form[field.name].touched && !!this.state.error[field.name]}
                                helperText={this.state.form[field.name].touched && this.state.error[field.name]}
                    />

                    <TextField  label="Last Name"
                                variant="outlined"
                                error={this.state.form[field.name].touched && !!this.state.error[field.name]}
                                helperText={this.state.form[field.name].touched && this.state.error[field.name]}
                    />

                    <TextField  label="Email Address"
                                variant="outlined"
                                error={this.state.form[field.name].touched && !!this.state.error[field.name]}
                                helperText={this.state.form[field.name].touched && this.state.error[field.name]}
                    />

                    <TextField  label="Phone"
                                variant="outlined"
                                error={this.state.form[field.name].touched && !!this.state.error[field.name]}
                                helperText={this.state.form[field.name].touched && this.state.error[field.name]}
                    />
                </form>
            </div>
        );
    }
};


export default TourScheduler;
