    //             activeMarker: null
    //         })
    //     }
    // };
    setKeyword = (event) => {
        // event.preventDefault();
        this.setState({
            keyword: 'restaurant',
            lat: 40.33,
            lng: -100.33
        });
        console.log('setKeyword: ');
        console.log(this.state.keyword);
        console.log(this.state.lat);
        console.log(this.state.lng);
        console.log('kkkkkk');

    }


    fetchPlaces(mapProps, map, value) {
        console.log('fetchPlaces start print');
        //
        console.log(mapProps);
        console.log(map);
        //
        console.log('fetchPlaces end print');

        const {google} = mapProps;


        const bounds = new google.maps.LatLngBounds();
        const infoWindow = new google.maps.InfoWindow;
        let currentInfoWindow = infoWindow;
        const service = new google.maps.places.PlacesService(map);
        let infoPane = document.getElementById('panel');
        let isNull = false;

        // try {
        //     console.log(this.state.keyword);
        //     console.log('try')
        // }
        // catch(err) {
        //
        //     isNull = true;
        //     console.log('still null');
        // }


        let request = {
            location: {
                lat: 40.33, lng: -74.57
            },
            rankBy: google.maps.places.RankBy.DISTANCE,

            // keyword: isNull ? 'hotel':this.state.keyword
            // keyword: this.state.keyword
            keyword: 'hotel'
        };

        console.log('...........');
        console.log(service);
        console.log('...........');
        // service.nearbySearch(request, this.nearbyCallback);





        service.nearbySearch(request, function(results, status) {
            if (status === google.maps.places.PlacesServiceStatus.OK) {
                console.log(status);
                results.forEach(place => {
                    let marker = new google.maps.Marker({
                        position: place.geometry.location,
                        map: map,
                        title: place.name
                    });


                    //     /* TODO: Step 4B: Add click listeners to the markers */
                    // Add click listener to each marker
                    google.maps.event.addListener(marker, 'click', () => {
                        let request = {
                            placeId: place.place_id,
                            fields: ['name', 'formatted_address', 'geometry', 'rating',
                                'website', 'photos']
                        };

                        /* Only fetch the details of a place when the user clicks on a marker.
                         * If we fetch the details for all place results as soon as we get
                         * the search response, we will hit API rate limits. */
                        service.getDetails(request, (placeResult, status) => {
                            if (status == google.maps.places.PlacesServiceStatus.OK) {
                                let placeInfowindow = new google.maps.InfoWindow();
                                let rating = "None";
                                if (placeResult.rating) rating = placeResult.rating;
                                placeInfowindow.setContent('<div><strong>' + placeResult.name +
                                    '</strong><br>' + 'Rating: ' + rating + '</div>');
                                placeInfowindow.open(marker.map, marker);
                                currentInfoWindow.close();
                                currentInfoWindow = placeInfowindow;
                                console.log("reach here");
                                // showPanel(placeResult);

                                if (infoPane.classList.contains("open"))
                                {
                                    infoPane.classList.remove("open");
                                }

                                // Clear the previous details
                                while (infoPane.lastChild) {
                                    infoPane.removeChild(infoPane.lastChild);
                                }

                                /* TODO: Step 4E: Display a Place Photo with the Place Details */
                                // Add the primary photo, if there is one
                                if (placeResult.photos) {
                                    let firstPhoto = placeResult.photos[0];
                                    let photo = document.createElement('img');
                                    photo.classList.add('hero');
                                    photo.src = firstPhoto.getUrl();
                                    infoPane.appendChild(photo);
                                }

                                // Add place details with text formatting
                                let name = document.createElement('h1');
                                name.classList.add('place');
                                name.textContent = placeResult.name;
                                infoPane.appendChild(name);
                                if (placeResult.rating) {
                                    let rating = document.createElement('p');
                                    rating.classList.add('details');
                                    rating.textContent = `Rating: ${placeResult.rating} \u272e`;
                                    infoPane.appendChild(rating);
                                }
                                let address = document.createElement('p');
                                address.classList.add('details');
                                address.textContent = placeResult.formatted_address;
                                infoPane.appendChild(address);
                                if (placeResult.website) {
                                    let websitePara = document.createElement('p');
                                    let websiteLink = document.createElement('a');
                                    let websiteUrl = document.createTextNode(placeResult.website);
                                    websiteLink.appendChild(websiteUrl);
                                    websiteLink.title = placeResult.website;
                                    websiteLink.href = placeResult.website;
                                    websitePara.appendChild(websiteLink);
                                    infoPane.appendChild(websitePara);
                                }

                                // Open the infoPane
                                infoPane.classList.add("open");


                            } else {
                                console.log('showDetails failed: ' + status);
                            }


                            // showDetails(placeResult, marker, status)
                        });
                    });

                    // Adjust the map bounds to include the location of this marker
                    bounds.extend(place.geometry.location);
                });
                /* Once all the markers have been placed, adjust the bounds of the map to
                 * show all the markers within the visible area. */
                map.fitBounds(bounds);


            }
            else {
                console.log("doesn't work");
            }
        });

    }


    setMapProps = (mapProps, map) => {
        this.setState(
            {
                mapProps: mapProps,
                map: map
            })
    }

    testProps = () => {
        // console.log('enter');
        // console.log(this.tRef.current);
        const {google} = this.tRef.current.props;

        let map = this.tRef.current.map;

        const bounds = new google.maps.LatLngBounds();
        const infoWindow = new google.maps.InfoWindow;
        let currentInfoWindow = infoWindow;
        const service = new google.maps.places.PlacesService(map);
        let infoPane = document.getElementById('panel');

        let request = {
            location: {
                lat: 40.33, lng: -74.57
            },
            rankBy: this.props.google.maps.places.RankBy.DISTANCE,

            // keyword: isNull ? 'hotel':this.state.keyword
            // keyword: this.state.keyword
            keyword: 'restaurant'
        };

        console.log('&&&&&&&&&&&');
        console.log(service);
        console.log('&&&&&&&&&&&');

        service.nearbySearch(request, function(results, status) {
            if (status === google.maps.places.PlacesServiceStatus.OK) {
                console.log(status);
                results.forEach(place => {
                    let marker = new google.maps.Marker({
                        position: place.geometry.location,
                        map: map,
                        title: place.name
                    });


                    //     /* TODO: Step 4B: Add click listeners to the markers */
                    // Add click listener to each marker
                    google.maps.event.addListener(marker, 'click', () => {
                        let request = {
                            placeId: place.place_id,
                            fields: ['name', 'formatted_address', 'geometry', 'rating',
                                'website', 'photos']
                        };

                        /* Only fetch the details of a place when the user clicks on a marker.
                         * If we fetch the details for all place results as soon as we get
                         * the search response, we will hit API rate limits. */
                        service.getDetails(request, (placeResult, status) => {
                            if (status == google.maps.places.PlacesServiceStatus.OK) {
                                let placeInfowindow = new google.maps.InfoWindow();
                                let rating = "None";
