import axios from "axios";
import {appConstants} from "../constants/constant";

const API = 'http://localhost:8089';

export const emailUs = (eu, success, failure) => {
    const emailUsPromise = axios.post(`${API}/emailUss`, eu)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });

    return {
        type: appConstants,
        payload: emailUsPromise
    };
};



export const getEmailUs = (tr, success, failure) => {
    const getEmailUsPromise = axios.get(`${API}/emailUss`, tr)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });

    return {
        type: appConstants,
        payload: getEmailUsPromise
    };
};
