import React, {Component} from "react";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
import Paper from "@material-ui/core/Paper/Paper";
import {Field, Form, Formik} from "formik";


const EditProductSchema = Yup.object().shape({
    firstName: Yup.string()
        .required('First name is required'),
    lastName: Yup.string()
        .required('Last brand is required'),
    emailAddress: Yup.string()
        .required('Email address is required'),
    phone: Yup.number()
        .required('Phone number is required'),
});





class TourScheduler extends Component {
    state = {
        floorPlan : '',
        date: new Date()
    }

    setFloorPlan = event => this.setState({floorPlan : event.target.value});

    handleChange = date => this.setState({ date });


    render() {
        return (
            <div>
                <p>Is there a particular floor plan you would like to tour?</p>
                <select onChange={this.setFloorPlan}>
                    <option>No, Thank you</option>
                    <option>1x1(1 bed, 1 bath)</option>
                    <option>2x2(2 bed, 2 bath)</option>
                    <option>3x3(3 bed, 3 bath)</option>
                    <option>4x4(4 bed, 4 bath)</option>
                </select>
                <p>{this.state.floorPlan}</p>
                <DatePicker
                    selected={this.state.date}
                    onChange={this.handleChange}
                    minDate={new Date((new Date()).valueOf() + 1000*3600*24)}
                />
                <Paper elevation={3} className="EditProduct" ref={this.wrapperRef}>
                    <h2>Last bit of information and we are all set</h2>
                    <Formik
                        // initialValues={this.props.product}
                        // onSubmit={this.handleSubmit}
                        validationSchema={EditProductSchema}
                    >
                        <Form className="form-group">
                            {
                                appConstants.PRODUCT_FIELDS.map(field => (
                                    <Field
                                        key={field.displayName}
                                        name={field.name}
                                        type={field.type}
                                        label={field.displayName}
                                        component={EditProduct.renderField}
                                    />
                                ))
                            }
                            <Button type="submit" color="primary">Schedule </Button>
                        </Form>
                    </Formik>
                </Paper>
            </div>
        );
    }
};


export default TourScheduler;
