import axios from "axios";
import {appConstants} from "../constants/constant";

const API = 'http://localhost:8089';

export const Application = (apply, success, failure) => {
    const applicationPromise = axios.post(`${API}/applications`, apply)
        .then(res => {
            res.data.success ?
                typeof success === 'function' && success() :
                typeof failure === 'function' && failure();
            return res;
        })
        .catch(err => {
            typeof failure === 'function' && failure(err);
            return err;
        });

    return {
        type: appConstants.APPLICATION,
        payload: applicationPromise
    };
};



export const getApplication = () => {
    const getApplicationPromise = axios.get(`${API}/applications`, {withCredentials: true});

    return {
        type: appConstants.GET_APPLICATION,
        payload: getApplicationPromise
    };
};
