import React, {useState} from 'react';
import Alert from "@material-ui/lab/Alert/Alert";
import Button from "@material-ui/core/Button/Button";
import {useDispatch} from "react-redux";
import {login} from "../actions/auth.action";
import {activate, deActivate} from "../actions/user.action";



const AdminActivation = () => {

    const dispatch = useDispatch();

    const [user_id, setUser_id] = useState(0);

    const handleInput = (event) => setUser_id(event.target.value);


    const handleActivate = () =>{
        dispatch(activate(
            user_id,
            () => {
                setOpen(true);
            },
            () => setOpen(true)
        ));
    }

    const handleDeActivate = () =>{
        dispatch(deActivate(
            user_id,
            () => {
                setOpen(true);
            },
            () => setOpen(true)
        ));
    }


    return(
        <div>
            <Alert severity="info">
                Please type the user_id that you want to activate
            </Alert>
            <input type="number" onChange={handleInput}/>
            <Button onClick = {handleActivate}>Activate the user</Button>
            <Button onClick = {handleDeActivate}>DeActivate the user</Button>

        </div>
    );


}

export default AdminActivation;
