import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import SendIcon from '@material-ui/icons/Send';
import DraftsIcon from '@material-ui/icons/Drafts';
import Maintenance from "./Maintenance";
import ApplicationBasicInfo from "./application/ApplicationBasicInfo";
import ApplicationRoommates from "./application/ApplicationRoomates";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    appBar: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    // necessary for content to be below app bar
    toolbar: theme.mixins.toolbar,
    content: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.default,
        padding: theme.spacing(3),
    },
}));

const UserPage = () => {
    const classes = useStyles();
    const [component, setComponent] = useState('user');


    return (
        <div className={classes.root}>
            <CssBaseline />
            {/*<AppBar position="fixed" className={classes.appBar}>*/}
            {/*<Toolbar>*/}
            {/*<Typography variant="h6" noWrap>*/}
            {/*Permanent drawer*/}
            {/*</Typography>*/}
            {/*</Toolbar>*/}
            {/*</AppBar>*/}
            <Drawer
                className={classes.drawer}
                variant="permanent"
                classes={{
                    paper: classes.drawerPaper,
                }}
                anchor="left"
            >
                <div className={classes.toolbar} />
                <List>
                    <ListItem button onClick={() => setComponent('application')}>
                        <ListItemIcon>
                            <SendIcon />
                        </ListItemIcon>
                        <ListItemText primary="Application" />
                    </ListItem>
                    <ListItem button onClick={() => setComponent('maintenance')}>
                        <ListItemIcon>
                            <DraftsIcon />
                        </ListItemIcon>
                        <ListItemText primary="Maintenance request" />
                    </ListItem>
                    <ListItem button onClick={() => setComponent('transactions')}>
                        <ListItemIcon>
                            <InboxIcon />
                        </ListItemIcon>
                        <ListItemText primary="Transactions" />
                    </ListItem>
                </List>
            </Drawer>
            <main className={classes.content}>
                <div className={classes.toolbar} />
                {
                    component === 'user' ?
                        <ApplicationBasicInfo />
                        :
                        component === 'maintenance' ?
                            <Maintenance />
                            :
                            <ApplicationRoommates />

                }


            </main>
        </div>
    );
}

export default UserPage;



