import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Alert from "@material-ui/lab/Alert/Alert";
import Button from "@material-ui/core/Button/Button";
import {useDispatch, useSelector} from "react-redux";
import {activate, deActivate} from "../actions/activate.action";
import {getUserInfos} from "../actions/userInfos.action";
import TableContainer from "@material-ui/core/TableContainer/TableContainer";
import Paper from "@material-ui/core/Paper/Paper";
import Table from "@material-ui/core/Table/Table";
import TableHead from "@material-ui/core/TableHead/TableHead";
import TableRow from "@material-ui/core/TableRow/TableRow";
import TableCell from "@material-ui/core/TableCell/TableCell";
import TableBody from "@material-ui/core/TableBody/TableBody";
import Snackbar from "@material-ui/core/Snackbar/Snackbar";
import {appConstants} from "../constants/constant";
import './AdminActivation.scss';

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const AdminActivation = () => {
    const classes = useStyles();

    const dispatch = useDispatch();
    const [user_id, setUser_id] = useState(0);
    const [search, setSearch] = useState({
        firstname: '',
        middlename: '',
        lastname: '',
    });

    const [open, setOpen] = useState(false);

    const [open2, setOpen2] = useState(false);

    const searchResultsState = useSelector(appState => {
        return {
            userInfos: appState.userInfos,
            response: appState.response,
            response2: appState.response2,
            msg: appState.response ?
                appConstants.ACTIVATE_SUCCESS_MESSAGE :
                appConstants.ACTIVATE_FAILURE_MESSAGE,

            msg2: appState.response2 ?
                appConstants.DEACTIVATE_SUCCESS_MESSAGE :
                appConstants.DEACTIVATE_FAILURE_MESSAGE,

        };
    });

    const [useData, setUseData] = React.useState(searchResultsState.userInfos);

    // (function () {
    //     console.log("ppppp:"+searchResultsState.response);
    // })();


    const f1 = (event) => {
        const newSearch = {...search};
        newSearch.firstname = event.target.value;
        setSearch(newSearch);
    };

    const f2 = (event) => {
        const newSearch = {...search};
        newSearch.middlename = event.target.value;
        setSearch(newSearch);
    };

    const f3 = (event) => {
        const newSearch = {...search};
        newSearch.lastname = event.target.value;
        setSearch(newSearch);
    };


    const handleInput = (event) =>
    {
        setUser_id(event.target.value);
    }

    const handleSubmit = (event) =>
    {
        event.preventDefault();
        dispatch(getUserInfos(search));
    }

    const showResults = (event) =>
    {
        event.preventDefault();
        setUseData(searchResultsState.userInfos);
    }

    const handleActivate = () =>{
        dispatch(activate(
            user_id,
            () => {
                setOpen(true);
            },
            () => {
                setOpen(true);
            }
        ));
    }

    const handleDeActivate = () =>{
        dispatch(deActivate(
            user_id,
            () => {
                setOpen2(true);
            },
            () => {
                setOpen2(true);
            }
        ));
    }

    return(
        <div>
            <Alert severity="info">
                Please type the user_id that you want to activate
            </Alert>
            <input type="number" onChange={handleInput}/>
            <Button color = "primary" onClick = {handleActivate}>Activate the user</Button>
            <Button color = "primary" onClick = {handleDeActivate}>DeActivate the user</Button>

            <div>
                <h3>Search User</h3>
                <label>First name:</label><br/>
                <input type="text" onChange={f1}/><br/>
                <label>Middle name:</label><br/>
                <input type="text" onChange={f2}/><br/>
                <label>Last name:</label><br/>
                <input type="text" onChange={f3}/>
                <br/>
                <Button color = "secondary" onClick = {handleSubmit}>Search</Button>
                <Button color = "secondary" onClick = {showResults}>Show Results</Button>
                {
                    useData ?
                            <TableContainer component={Paper}>
                                <Table className={classes.table} aria-label="simple table">
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>First Name</TableCell>
                                            <TableCell align="left">Middle Name</TableCell>
                                            <TableCell align="left">Last Name</TableCell>
                                            <TableCell align="left">SSN</TableCell>
                                            <TableCell align="left">Address1</TableCell>
                                            <TableCell align="left">Address2</TableCell>
                                            <TableCell align="left">Zip</TableCell>
                                            <TableCell align="left">City</TableCell>
                                            <TableCell align="left">State</TableCell>
                                            <TableCell align="left">Birth Date</TableCell>
                                            <TableCell align="left">Email</TableCell>
                                            <TableCell align="left">User_ID</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {useData.map((row) => (
                                            <TableRow key={row.id}>
                                                <TableCell component="th" scope="row">
                                                    {row.firstname}
                                                </TableCell>
                                                <TableCell align="left">{row.middlename}</TableCell>
                                                <TableCell align="left">{row.lastname}</TableCell>
                                                <TableCell align="left">{row.ssn}</TableCell>
                                                <TableCell align="left">{row.address1}</TableCell>
                                                <TableCell align="left">{row.address2}</TableCell>
                                                <TableCell align="left">{row.zip}</TableCell>
                                                <TableCell align="left">{row.city}</TableCell>
                                                <TableCell align="left">{row.state}</TableCell>
                                                <TableCell align="left">{row.birthdate}</TableCell>
                                                <TableCell align="left">{row.email}</TableCell>
                                                <TableCell align="left">{row.user_id}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                            :
                            <span></span>
                     }
                <Snackbar
                    // className={loginState.user ? console.log(loginState.user.profiles[0].id) : 'failure'}
                    className={searchResultsState.response ? 'success' : 'failure'}
                    open={open}
                    onClose={() => setOpen(false)}
                    autoHideDuration={3000}
                    message={<span>{searchResultsState.msg}</span>}
                />
                <Snackbar
                    // className={loginState.user ? console.log(loginState.user.profiles[0].id) : 'failure'}
                    className={searchResultsState.response2 ? 'success' : 'failure'}
                    open={open2}
                    onClose={() => setOpen2(false)}
                    autoHideDuration={3000}
                    message={<span>{searchResultsState.msg2}</span>}
                />
            </div>
        </div>
    );


}

export default AdminActivation;
