import React, {useState} from 'react';
import Button from "@material-ui/core/Button/Button";
import TableContainer from "@material-ui/core/TableContainer/TableContainer";
import Paper from "@material-ui/core/Paper/Paper";
import Table from "@material-ui/core/Table/Table";
import TableHead from "@material-ui/core/TableHead/TableHead";
import TableRow from "@material-ui/core/TableRow/TableRow";
import TableCell from "@material-ui/core/TableCell/TableCell";
import TableBody from "@material-ui/core/TableBody/TableBody";
import {useDispatch, useSelector} from "react-redux";
import {getUserInfo} from "../actions/userInfo.action";
import {getActiveApplications} from "../actions/activeApplications";
import {makeStyles} from "@material-ui/core";

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});


const AdminSearch = () => {
    const classes = useStyles();

    const dispatch = useDispatch();
    const [user_id, setUser_id] = useState(0);

    const AdminSearchState = useSelector(appState => {
        return {
            userInfo: appState.userInfo,
            activeApplications: appState.activeApplications
        };
    });

    const [useData, setUseData] = React.useState(AdminSearchState.userInfo);

    const [useData2, setUseData2] = React.useState(AdminSearchState.activeApplications);

    const handleInput = (event) =>
    {
        setUser_id(event.target.value);
    }

    const handleSubmit = (event) =>
    {
        event.preventDefault();
        dispatch(getUserInfo(user_id));
    }

    const showResults = (event) =>
    {
        event.preventDefault();
        setUseData(AdminSearchState.userInfo);
    }

    const handleSubmit2 = (event) =>
    {
        event.preventDefault();
        dispatch(getActiveApplications());
    }

    const showResults2 = (event) =>
    {
        event.preventDefault();
        setUseData2(AdminSearchState.activeApplications);
    }


    return (
        <div>
            <h3>Search User by User_id</h3>
            <label>Uid:</label><br/>
            <input type="number" onChange={handleInput}/><br/>
            <br/>
            <Button color = "secondary" onClick = {handleSubmit}>Search</Button>
            <Button color = "secondary" onClick = {showResults}>Show Results</Button>
            {
                useData ?
                    <TableContainer component={Paper}>
                        <Table className={classes.table} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>First Name</TableCell>
                                    <TableCell align="left">Middle Name</TableCell>
                                    <TableCell align="left">Last Name</TableCell>
                                    <TableCell align="left">SSN</TableCell>
                                    <TableCell align="left">Address1</TableCell>
                                    <TableCell align="left">Address2</TableCell>
                                    <TableCell align="left">Zip</TableCell>
                                    <TableCell align="left">City</TableCell>
                                    <TableCell align="left">State</TableCell>
                                    <TableCell align="left">Birth Date</TableCell>
                                    <TableCell align="left">Email</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                    <TableRow key={useData.id}>
                                        <TableCell component="th" scope="row">
                                            {useData.firstname}
                                        </TableCell>
                                        <TableCell align="left">{useData.middlename}</TableCell>
                                        <TableCell align="left">{useData.lastname}</TableCell>
                                        <TableCell align="left">{useData.ssn}</TableCell>
                                        <TableCell align="left">{useData.address1}</TableCell>
                                        <TableCell align="left">{useData.address2}</TableCell>
                                        <TableCell align="left">{useData.zip}</TableCell>
                                        <TableCell align="left">{useData.city}</TableCell>
                                        <TableCell align="left">{useData.state}</TableCell>
                                        <TableCell align="left">{useData.birthdate}</TableCell>
                                        <TableCell align="left">{useData.email}</TableCell>
                                    </TableRow>
                            </TableBody>
                        </Table>
                    </TableContainer>
                    :
                    <span></span>
            }
            <h3>Get all active residents</h3>
            <Button color = "secondary" onClick = {handleSubmit2}>Get</Button>
            <Button color = "secondary" onClick = {showResults2}>Show Results</Button>
            {
                useData2 ?
                    <TableContainer component={Paper}>
                        <Table className={classes.table} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>User id</TableCell>
                                    <TableCell align="left">Room number</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {useData2.map((row) => (
                                    <TableRow key={row.id}>
                                        <TableCell align="left">{row.user_id}</TableCell>
                                        <TableCell align="left">{row.roomnum}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                    :
                    <span></span>
            }
        </div>
    );

}

export default AdminSearch;
