import React from 'react';
import Button from "@material-ui/core/Button/Button";
import Dialog from "@material-ui/core/Dialog/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText/DialogContentText";
import TextField from "@material-ui/core/TextField/TextField";
import DialogActions from "@material-ui/core/DialogActions/DialogActions";
import {makeStyles} from "@material-ui/core";
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import FormatAlignLeftIcon from '@material-ui/icons/FormatAlignLeft';
import FormatAlignCenterIcon from '@material-ui/icons/FormatAlignCenter';
import FormatAlignRightIcon from '@material-ui/icons/FormatAlignRight';
import FormatAlignJustifyIcon from '@material-ui/icons/FormatAlignJustify';
import clsx from "clsx";

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    margin: {
        margin: theme.spacing(1),
    },
    withoutLabel: {
        marginTop: theme.spacing(3),
    },
    textField: {
        width: '25ch',
    },
}));




export default function EmailUs() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const [alignment, setAlignment] = React.useState('left');

    const handleAlignment = (event, newAlignment) => {
        setAlignment(newAlignment);
    };

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
    <div>
        <Button variant="outlined" color="default" onClick={handleClickOpen}>
            Email Us
        </Button>
        <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Send us a message</DialogTitle>
            <DialogContent>
                <TextField
                    className={clsx(classes.margin, classes.textField)}
                    autoFocus
                    margin="dense"
                    required id="first-name"
                    label="First Name"
                />
                <TextField
                    className={clsx(classes.margin, classes.textField)}
                    autoFocus
                    margin="dense"
                    required id="last-name"
                    label="Last Name"
                />
                <TextField
                    autoFocus
                    margin="dense"
                    required id="email"
                    label="Email Address"
                    type="email"
                    fullWidth
                />
                <TextField
                    autoFocus
                    margin="dense"
                    required id="phone"
                    label="Phone"
                    type="number"
                    fullWidth
                />

                <br/>
                <br/>

                <DialogContentText>
                    Would you like us to send you SMS alert?
                </DialogContentText>

                <ToggleButtonGroup
                    value={alignment}
                    exclusive
                    onChange={handleAlignment}
                    aria-label="text alignment"
                >
                    <ToggleButton value="yes" aria-label="yes-b">
                        Yes
                    </ToggleButton>
                    <ToggleButton value="no" aria-label="no-b">
                        No
                    </ToggleButton>

                </ToggleButtonGroup>
                <TextField
                    autoFocus
                    margin="dense"
                    id="message"
                    label="Message"
                    multiline
                    fullWidth
                    rows={4}
                    helperText={"Message is optional"}
                />
            </DialogContent>
            <DialogActions>
                <Button onClick={handleClose} color="primary">
                    Send
                </Button>
            </DialogActions>
        </Dialog>
    </div>
);
}
