import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ApplicationBasicInfo from "./ApplicationBasicInfo";
import ApplicationUnitInfo from "./ApplicationUnitInfo";
import ApplicationRoommates from "./ApplicationRoomates";

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    top:{
        width: '100%',
    },
    middle: {
        width: '100%',
    },
    centerDiv: {
        width: '100%',
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    backButton: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
}));


export default function HorizontalLabelPositionBelowStepper() {
    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();


    const [apply, setApply] = useState({
        firstname: '',
        middlename: '',
        lastname: '',
        birthdate: '',
        ssn: '',
        email: '',
        phone: '',
        address1: '',
        address2: '',
        state: '',
        city: '',
        zip: '',
        leaseterm: '',
        floorplan: '',
        preferedroommates:'',
        gender: '',
        roommategender: '',
        smoker: '',
        drinker: '',
        comfortableanimal: '',
        neat: '',
        quite: '',

        date: '',
        status: 'not replied'
    });


    function getSteps() {
        return ['Basic Info', 'Unit Info', 'Roommates'];
    }


    function getStepContent(stepIndex) {
        switch (stepIndex) {
            case 0:
                return <ApplicationBasicInfo apply={apply}  setApply={setApply}/>;
            case 1:
                return <ApplicationUnitInfo apply={apply} setApply={setApply}/>;
            case 2:
                return <ApplicationRoommates apply={apply} setApply={setApply}/>;
            default:
                return 'Unknown stepIndex';
        }
    }


    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };

    return (
        <div className={classes.root}>
            <div className={classes.top}>
                <Stepper activeStep={activeStep} alternativeLabel>
                    {steps.map((label) => (
                        <Step key={label}>
                            <StepLabel>{label}</StepLabel>
                        </Step>
                    ))}
                </Stepper>
            </div>
            <div className={classes.middle}>
                {activeStep === steps.length ? (
                    <div>
                        <Typography className={classes.instructions}>All steps completed</Typography>
                        <Button onClick={handleReset}>Reset</Button>
                    </div>
                ) : (
                    <div className={classes.centerDiv}>
                        <div>
                            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
                        </div>
                    </div>
                )}
            </div>
            <br/>
            <br/>
            <br/>
            <div className={classes.centerDiv}>
                <Button
                    disabled={activeStep === 0}
                    onClick={handleBack}
                    className={classes.backButton}
                >
                    Back
                </Button>
                <Button variant="contained" color="primary" onClick={handleNext}>
                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                </Button>
            </div>
        </div>
    );
}
