import {appConstants} from "../constants/constant";


export const responseReducer = (state = null, action) => {
    switch(action.type) {
        case appConstants.PUT_APPLICATION:
            return action.payload.success ? action.payload : null;
        case appConstants.ACTIVATE:
            return action.payload.success ? action.payload : null;
        case appConstants.DE_ACTIVATE:
            return action.payload.success ? action.payload : null;
        default:
            return state;
    }
}
