import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import {useDispatch, useSelector} from "react-redux";
import {getPayments} from "../actions/userPayments.action";
import Button from "@material-ui/core/Button/Button";
import {getPendingPayments} from "../actions/userPendingPayments";
import {appConstants} from "../constants/constant";
import Collapse from "@material-ui/core/Collapse/Collapse";
import DialogTitle from "@material-ui/core/DialogTitle/DialogTitle";
import Dialog from "@material-ui/core/Dialog/Dialog";
import DialogContent from "@material-ui/core/DialogContent/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText/DialogContentText";
import DialogActions from "@material-ui/core/DialogActions/DialogActions";


const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});


const Transaction = () => {
    const classes = useStyles();
    const dispatch = useDispatch();
    const transactionState = useSelector(appState => {
        return {
            user: appState.user,
            userPayments: appState.userPayments

        };
    });

    const [checked, setChecked] = React.useState(false);

    const handleChange = () => {
        setChecked((prev) => !prev);
        console.log(transactionState.userPayments);
    };

    const pay = () => {
        return (
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">{"Use Google's location service?"}</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Let Google help apps determine location. This means sending anonymous location data to
                        Google, even when no apps are running.
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Disagree
                    </Button>
                    <Button onClick={handleClose} color="primary" autoFocus>
                        Agree
                    </Button>
                </DialogActions>
            </Dialog>
        )
    }



        return (
            <div>
            <div>
                <Button onClick = {handleChange}>Show my payment histories</Button>
                <Collapse in={checked}>
                    <TableContainer component={Paper}>
                        <Table className={classes.table} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Amount $</TableCell>
                                    <TableCell align="left">Memo</TableCell>
                                    <TableCell align="left">Date</TableCell>
                                    <TableCell align="left">Status</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {
                                    transactionState.userPayments ?

                                    transactionState.userPayments.map((row) => (
                                    <TableRow key={row.id}>
                                        <TableCell component="th" scope="row">
                                            {row.amount}
                                        </TableCell>
                                        <TableCell align="left">{row.memo}</TableCell>
                                        <TableCell align="left">{row.date}</TableCell>
                                        {
                                            row.status === 'unpaid' ?
                                                <TableCell align="left">
                                                    <div>
                                                        {row.status}
                                                        <Button color = "secondary" onClick={pay}>Pay</Button>
                                                    </div>
                                                </TableCell>
                                                :
                                                <TableCell align="left">{row.status}</TableCell>
                                        }
                                    </TableRow>

                                    ))
                                    :
                                        <span></span>

                                }
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Collapse>


            </div>
            <div>
            </div>
            </div>
        );



}

export default Transaction;
