import React from 'react';
import Alert from "@material-ui/lab/Alert/Alert";
import AlertTitle from "@material-ui/lab/AlertTitle/AlertTitle";
import Container from "@material-ui/core/Container/Container";
import FormControl from "@material-ui/core/FormControl/FormControl";
import { makeStyles } from '@material-ui/core/styles';
import TextField from "@material-ui/core/TextField/TextField";
import clsx from "clsx";
import Button from "@material-ui/core/Button/Button";


const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    margin: {
        margin: theme.spacing(1),
    },
    textField: {
        width: '25ch',
    },
    button: {
        alignItems: "right"
        // bottom: 20,
        // left: 'auto',
        // position: 'fixed',
    }
}));

const Maintenance = () => {
    const classes = useStyles();
    const [values, setValues] = React.useState({
        name:'',
        roomNum: '',
        doorNum: '',
        issue: '',
        date: new Date(),
        status: 'not finished'
    });


    const f1 = (event) => {
        const newValue = {...values};
        newValue.name = event.target.value;
        setValues(newValue);
    };

    const f2 = (event) => {
        const newValue = {...values};
        newValue.roomNum = event.target.value;
        setValues(newValue);
    };

    const f3 = (event) => {
        const newValue = {...values};
        newValue.doorNum = event.target.value;
        setValues(newValue);
    };


    const f4 = (event) => {
        const newValue = {...values};
        newValue.issue = event.target.value;
        setValues(newValue);
    };


    return(
        <Container>
            <Alert severity="info">
            <AlertTitle>Info</AlertTitle>
            Please describe the issues <strong>as detail as</strong> you could, thanks
            </Alert>
                <TextField
                    className={clsx(classes.margin, classes.textField)}
                    autoFocus
                    margin="dense"
                    required id="name"
                    label="Name"
                    value={values.name}
                    onChange={f1}

                />
                <TextField
                    className={clsx(classes.margin, classes.textField)}
                    autoFocus
                    margin="dense"
                    required id="roomNum"
                    label="Room number"
                    value={values.roomNum}
                    onChange={f2}

                />
                <TextField
                    className={clsx(classes.margin, classes.textField)}
                    autoFocus
                    margin="dense"
                    required id="doorNum"
                    label="Door number"
                    type="text"
                    value={values.doorNum}
                    onChange={f3}
                />
            <FormControl fullWidth className={classes.margin}>
                <TextField
                    autoFocus
                    label="type here"
                    id="requests"
                    variant="outlined"
                    multiline
                    rows={10}
                    onChange={f4}
                />
            </FormControl>
            <Button className={classes.button}>
                Submit
            </Button>
        </Container>
    );

}

export default Maintenance;
