import React, {Component, useState} from 'react';
import {Card, CardBody,  Button, Collapse} from 'reactstrap';
import './FAQ.scss';



export default class FAQ extends Component {
    constructor(props) {
        super(props);
        this.state = {
            collapse: true,
        };
        this.dropdown = this.dropdown.bind(this);
        this.text =  this.text.bind(this);
    }

    dropdown() {

        console.log(this.state.collapse);
        this.setState({collapse: !this.state.collapse})
    }

    text() {
        console.log('text');
        return(
            <p>You are welcome to bring additional furniture with you, but don’t forget that we fully furnished all of our apartments. If you wish to replace some of our furniture and use yours instead, you may be required to rent a storage space here on the property to store our furnishing. Storages are available in different sizes and
                in varied locations. The best part is the price –
                they only run $5, $10 or $15 a month.</p>
        );

    }
    render() {
        return (
            <div >
                <h1>General Leasing Questions</h1>

                <p>Can additional furniture be brought into the apartment?</p>
                <p>Can I pay my bill with a credit card? Is there an extra charge?</p>
                <p>Do I have a meal plan?</p>
                <p>Do you allow pets?</p>
                <p>How are roommates matched?</p>
                <p>How many people live there?</p>
                <p>If I am over 21 do I need a guarantor?</p>
                <p>Is parking available?</p>
                <p>What if I need to cancel my lease?</p>
                <p>What is the cost?</p>
                <p>What size are the beds?</p>
                <p>What types of leases are available?</p>
                <p>When can I move in?</p>
                <p>When do I put down a deposit?</p>



                <div>
                <p onClick={this.dropdown} size='lg'>Can additional furniture be brought into the apartment?</p>
                <Collapse isOpen={this.state.collapse}>

                    <Card>
                        <CardBody>
                            {this.state.collapse ? this.text() : <span></span>}
                        </CardBody>
                    </Card>
                </Collapse>
                </div>



            </div>);
    }
}




