import React, {Component} from "react";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

class TourScheduler extends Component {
    state = {
        floorPlan : '',
        date: new Date()
    }

    // onChange={(values) => this.setValues(values)}

    setFloorPlan = (event) =>
    {
        this.setState({floorPlan : event.target.value});
    }

    onChange = date => this.setState({ date });
    // function onChangeDate(event)
    // {
    //     state.date = event.target.value;
    // }

    render() {
        return (
            <div>
                <p>Is there a particular floor plan you would like to tour?</p>
                <select onChange={this.setFloorPlan}>
                    <option>No, Thank you</option>
                    <option>1x1(1 bed, 1 bath)</option>
                    <option>2x2(2 bed, 2 bath)</option>
                    <option>3x3(3 bed, 3 bath)</option>
                    <option>4x4(4 bed, 4 bath)</option>
                </select>
                <p>{this.state.date}</p>
                <Calendar
                    onChange={this.onChange}
                    // defaultActiveStartDate = {state.date}
                    value={this.state.date}
                />
            </div>
        );
    }
};


export default TourScheduler;
